import { DatePipe } from '@angular/common';
import { HttpClient } from '@angular/common/http';
import { AfterViewInit, Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { environment } from 'src/environments/environment';
import * as CryptoJS from 'crypto-js';

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.scss']
})
export class PaymentComponent implements AfterViewInit {
  params = new Map();
  constructor(    
    private formBuilder: FormBuilder, private http: HttpClient, private datePipe: DatePipe
  ) {}

  ngAfterViewInit() {
    this.postToExternalSite();
  }
  // create a form for the post request
  postToExternalSite(): void {
    const form = window.document.createElement('form');
    form.setAttribute('method', 'post');
    form.setAttribute('action', 'https://testsecureacceptance.cybersource.com/embedded/pay');
    // use _self to redirect in same tab, _blank to open in new tab
    // form.setAttribute('target', '_blank');
    form.setAttribute('target', 'sa_iframe');

    // Add all the data to be posted as Hidden elements

    form.appendChild(this.createHiddenElement('access_key', 'c66502af927a3533bfa6d215394eda4c'));
    form.appendChild(this.createHiddenElement('profile_id', 'B63C4A44-D1AE-4F14-B0D5-4FFC7813736F'));
    form.appendChild(this.createHiddenElement('transaction_uuid', '649093fec08e3'));
    form.appendChild(this.createHiddenElement('signed_field_names', 'access_key,profile_id,transaction_uuid,signed_field_names,unsigned_field_names,signed_date_time,locale,transaction_type,reference_number,amount,currency'));
    form.appendChild(this.createHiddenElement('unsigned_field_names', ''));
    form.appendChild(this.createHiddenElement('signed_date_time', '2023-06-19T18:28:19Z'));
    form.appendChild(this.createHiddenElement('locale', 'en-US'));
    form.appendChild(this.createHiddenElement('transaction_type', 'authorization'));
    form.appendChild(this.createHiddenElement('amount', '25'));
    form.appendChild(this.createHiddenElement('reference_number', '123456789'));
    form.appendChild(this.createHiddenElement('currency', 'usd'));   
    this.sign(this.params);

    form.appendChild(this.createHiddenElement('signature', 'DXRaGtqAXiLJPQxujjaJdOtQfpS4Kde93VxZrFHEsVc='));    
    console.log(form);
    window.document.body.appendChild(form);
    form.submit();
  }


  // create the form
  private createHiddenElement(name: string, value: string): HTMLInputElement {
    const hiddenField = document.createElement('input');
    hiddenField.setAttribute('name', name);
    hiddenField.setAttribute('value', value);
    hiddenField.setAttribute('type', 'hidden');
    this.params.set(name, value);
    return hiddenField;
  }

  private dateString(): string {
    return this.datePipe.transform(Date.now(), 'yyyy-MM-ddTHH:mm:ss') + "Z";
  }

  public sign(params: any): string {
    let dataToSign = '';
    this.params.forEach((value: string, key: string) => {
      dataToSign += key + "=" + value + ","
    });
    dataToSign = dataToSign.substring(0, dataToSign.length - 1);
    let hardString = "access_key=c66502af927a3533bfa6d215394eda4c,profile_id=B63C4A44-D1AE-4F14-B0D5-4FFC7813736F,transaction_uuid=64909e435727c,signed_field_names=access_key,profile_id,transaction_uuid,signed_field_names,unsigned_field_names,signed_date_time,locale,transaction_type,reference_number,amount,currency,unsigned_field_names=,signed_date_time=2023-06-19T18:28:19Z,locale=en-US,transaction_type=authorization,reference_number=123456789,amount=25,currency=usd";
    console.log(dataToSign);
    console.log(hardString);
    console.log(dataToSign === hardString);
    this.signData("access_key=c66502af927a3533bfa6d215394eda4c,profile_id=B63C4A44-D1AE-4F14-B0D5-4FFC7813736F,transaction_uuid=64909e435727c,signed_field_names=access_key,profile_id,transaction_uuid,signed_field_names,unsigned_field_names,signed_date_time,locale,transaction_type,reference_number,amount,currency,unsigned_field_names=,signed_date_time=2023-06-19T18:28:19Z,locale=en-US,transaction_type=authorization,reference_number=123456789,amount=25,currency=usd", environment.SECRET_KEY);
    return this.signData(dataToSign, environment.SECRET_KEY);
  }

  public signData(data: string, secretKey: string): string {
      const signatureString = CryptoJS.HmacSHA256(data, secretKey).toString(CryptoJS.enc.Base64);
      console.log(signatureString);
      return signatureString;
  }

}
