import { Component } from '@angular/core';

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.scss']
})
export class PaymentComponent {
  paymentForm = document.getElementById('payment_form') as HTMLFormElement;
  accessKey = 'c66502af927a3533bfa6d215394eda4c';
  profileId = 'B63C4A44-D1AE-4F14-B0D5-4FFC7813736F';
  transactionUuid = Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);
  signedFieldNames = 'access_key,profile_id,transaction_uuid,signed_field_names,unsigned_field_names,signed_date_time,locale,transaction_type,reference_number,amount,currency';
  signedDateTime = new Date();
  locale = 'en-US';


}
