import { DatePipe } from '@angular/common';
import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { environment } from 'src/environments/environment';

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.scss']
})
export class PaymentComponent {
  constructor(    
    private formBuilder: FormBuilder, private http: HttpClient, private datePipe: DatePipe
  ) {this.postToExternalSite()}
  // create a form for the post request
  postToExternalSite(): void {
    const form = window.document.createElement('form');
    form.setAttribute('method', 'post');
    form.setAttribute('action', environment.endpoint);
    // use _self to redirect in same tab, _blank to open in new tab
    // form.setAttribute('target', '_blank');
    form.setAttribute('target', 'frame');

    // Add all the data to be posted as Hidden elements

    form.appendChild(this.createHiddenElement('access_key', 'c66502af927a3533bfa6d215394eda4c'));
    form.appendChild(this.createHiddenElement('profile_id', 'B63C4A44-D1AE-4F14-B0D5-4FFC7813736F'));
    form.appendChild(this.createHiddenElement('transaction_uuid',Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15)));
    form.appendChild(this.createHiddenElement('signed_field_names', 'access_key,profile_id,transaction_uuid,signed_field_names,unsigned_field_names,signed_date_time,locale,transaction_type,reference_number,amount,currency'));
    form.appendChild(this.createHiddenElement('unsigned_field_names', ''));
    form.appendChild(this.createHiddenElement('signed_date_time', this.dateString()));
    form.appendChild(this.createHiddenElement('locale', 'en-US'));
    form.appendChild(this.createHiddenElement('transaction_type', 'authorization'));
    form.appendChild(this.createHiddenElement('amount', '25'));
    form.appendChild(this.createHiddenElement('reference_number', '123456789'));
    form.appendChild(this.createHiddenElement('currency', 'usd'));
    form.appendChild(this.createHiddenElement('submit', 'Submit'));
    form.appendChild(this.createHiddenElement('signature', 'WA5WVL5S0RHlMoRtXlA+LxJ+d9aPanANCzmfCjAAHq4='));    
    console.log(form);
    window.document.body.appendChild(form);
    form.submit();
  }


  // create the form
  private createHiddenElement(name: string, value: string): HTMLInputElement {
    const hiddenField = document.createElement('input');
    hiddenField.setAttribute('name', name);
    hiddenField.setAttribute('value', value);
    hiddenField.setAttribute('type', 'hidden');
    return hiddenField;
  }

  private dateString(): string {
    return this.datePipe.transform(Date.now(), 'yyyy-MM-ddTHH:mm:ss') + "Z";
  }
}
